var $document=$(document)
var hash={}
var setup=!1
$.event.special.clickout={setup:function(data,namespaces,eventHandle){if(setup)return
$document.on('click.clickout-handler',function(event){$.each(hash,function(index,obj){var condition=!0
$.each(obj.elements,function(i,el){if($(event.target).closest(el).length)condition=!1})
if(condition){obj.handler.call($(obj.elements),event)}})})
setup=!0},teardown:function(){if(hash)return
$document.off('click.clickout-handler')
setup=!1},add:function(handleObj){var guid=handleObj.guid
if(!hash.hasOwnProperty(guid)){hash[guid]={elements:[this],handler:handleObj.handler}}else{hash[guid].elements.push(this)}},remove:function(handleObj){delete hash[handleObj.guid]}}